import './Home.css'
import Sylvia from '../img/Sylvia.JPG'
import CarouselControll from './Carousel'
import signiture from '../img/signiture1.png'
import { Container, Row, Col } from 'react-bootstrap'
import Granim from "granim"
import CustomCursorContext from './CursorContext.ts'
import  React ,{useEffect, useContext} from 'react'
// import StarfieldAnimation from 'react-starfield-animation'


const Home = () => {

    const { type } = useContext(CustomCursorContext);
    const secondaryCursor = React.useRef(null);
    const mainCursor = React.useRef(null);
    const positionRef = React.useRef({
      mouseX: 0,
      mouseY: 0,
      destinationX: 0,
      destinationY: 0,
      distanceX: 0,
      distanceY: 0,
      key: -1,
    });

    React.useEffect(() => {
      document.addEventListener("mousemove", (event) => {
        const { clientX, clientY } = event;

        const mouseX = clientX;
        const mouseY = clientY;

        positionRef.current.mouseX =
          mouseX - secondaryCursor.current.clientWidth / 2;
        positionRef.current.mouseY =
          mouseY - secondaryCursor.current.clientHeight / 2;
        mainCursor.current.style.transform = `translate3d(${mouseX -
          mainCursor.current.clientWidth / 2}px, ${mouseY -
          mainCursor.current.clientHeight / 2}px, 0)`;
      });

      return () => {};
    }, []);

    React.useEffect(() => {
      const followMouse = () => {
        positionRef.current.key = requestAnimationFrame(followMouse);
        const {
          mouseX,
          mouseY,
          destinationX,
          destinationY,
          distanceX,
          distanceY,
        } = positionRef.current;
        if (!destinationX || !destinationY) {
          positionRef.current.destinationX = mouseX;
          positionRef.current.destinationY = mouseY;
        } else {
          positionRef.current.distanceX = (mouseX - destinationX) * 0.1;
          positionRef.current.distanceY = (mouseY - destinationY) * 0.1;
          if (
            Math.abs(positionRef.current.distanceX) +
              Math.abs(positionRef.current.distanceY) <
            0.1
          ) {
            positionRef.current.destinationX = mouseX;
            positionRef.current.destinationY = mouseY;
          } else {
            positionRef.current.destinationX += distanceX;
            positionRef.current.destinationY += distanceY;
          }
        }
        secondaryCursor.current.style.transform = `translate3d(${destinationX}px, ${destinationY}px, 0)`;
      };
      followMouse();
    }, []);



    // const perf = require('./HomeScript.html');

    // try{  
    // useEffect(() => {
    //     new Granim({
    //       element: "#logo-canvas",
    //       direction: "left-right",
    //       opacity: [1, 1],
    //       states: {
    //         "default-state": {
    //           gradients: [
    //             ["#ee9ca7", "#0f9b0f"],
    //             ["#ffdde1", "#ff9a9e"],
    //             ["#ff7b59", "#ff4d00"],
    //             ["#ff9a9e", "#ffdde1"],
    //             ["#ff4d00", "#ff7b59"],
    //             ["#ffdde1", "#ff9a9e"],
    //           ],
    //           transitionSpeed: 2000,
    //         },
    //       },
    //     });
    //   }, []);
    // }
    // catch(error){
    //     console.log("error here!!!!:", error)
    // }

    return(

        <div className="intro">

            <div className='main-cursor' ref={mainCursor}></div>
            <div className='secondary-cursor' ref={secondaryCursor}></div>
            {/* <StarfieldAnimation
                style={{
                position: 'absolute',
                width: '100%',
                height: '100%'
                }}
            /> */}


            {/* <Container>
                <Row>

                    <Col>
                        <div className="intro_photo" >
                            <img src={Sylvia}></img>
                        </div>
                    </Col>

                    <Col> */}
                        <div className='intro_text' >
                            <div> hi stranger, thank you for dropping by  </div>
                                <div id='violet'>this site is about someone named sylvia(siyu) chen, who</div> 
                                    <div>writes <span>code</span></div>
                                    <div>takes photos</div>
                                    <div>reads albert camus</div>
                                    <div>types in lowercase</div>
                            <div>and welcomes bribes in the form of a leica lens or Kodak 35mm roll films </div>
                        </div>
                        <img className='sig' src={signiture} size={40}/>
                    {/* </Col>

                </Row>
            </Container> */}

            <div className='intro-project'>
                {/* <CarouselControll/> */}
            </div>
        </div>

    )
}


export default Home
